import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Productpage = () => {
  const [products, setProducts] = useState([]);
  const { productID } = useParams();

  useEffect(() => {
    fetch("/iphones.json")
      .then((res) => res.json())
      .then((data) => {
        const productList = data.products;
        const singleProduct = productList.filter(
          (x) => x.product_url === productID
        );
        setProducts(singleProduct);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [productID]);

  if (products.length === 0) {
    // Handle case where no product is found with the specified productID
    return <div>No product found</div>;
  }

  const {
    product_url: id,
    product_name: title,
    product_img: img,
    product_brief_description: Brief,
    starting_price: StartPrice,
    price_range: PriceRange,
    product_description: details,
  } = products[0];

  return (
    <div>
      <section className="internal-page-wrapper top-100">
        <div className="container">
          <div className="bottom-100">
            <div className="row justify-content-center text-center bottom-50">
              <div className="col-12">
                <div className="title-wraper bold">{title}</div>
                <div className="brief-description">{Brief}</div>
              </div>
            </div>

            <div className="row justify-content-center text-center product-holder h-100">
              <div className="col-sm-12 col-md-6 my-auto">
                <div className="starting-price">{`Starting at ${StartPrice}`}</div>
                <div className="monthly-price">{PriceRange}</div>
                <div className="product-details">{details}</div>
              </div>

              <div className="col-sm-12 col-md-6">
                <div className="product-image">
                  <img src={img} alt={title} />
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Productpage;
